mapper.select:
SELECT image_id, member_id, place_id, course_place_id, originalName, savedName
FROM image
WHERE image_id = #{imageId}

SELECT image_id, member_id, place_id, course_place_id, originalName, savedName
FROM image
WHERE course_place_id = #{coursePlaceId}

SELECT image_id, member_id, place_id, course_place_id, originalName, savedName
FROM image
WHERE place_id = #{placeId}

SELECT image_id, member_id, place_id, course_place_id, originalName, savedName
FROM image
WHERE member_id = #{memberId}

SELECT image_id, member_id, place_id, course_place_id, course_id, original_name, saved_name
FROM image
WHERE course_id = #{courseId}

SELECT image_id, member_id, place_id, course_place_id, course_id, original_name, saved_name
FROM image
WHERE image_id = #{imageId}

mapper.update:
UPDATE image
SET
    original_name = #{originalName},
    saved_name = #{savedName}
WHERE image_id = #{imageId}
